import React, { useState } from 'react';
import axios from '../utils/axios';
import { Popup, Circle, LayersControl, FeatureGroup, Marker } from 'react-leaflet';
import L, { map } from 'leaflet';
import { useDispatch } from 'react-redux';
import { removeMarker } from '../redux/features/markerSlice';
import greenMarker from '../assets/markers/green_marker.png';


const NewMarker = ({ _id, category, markerName, description, position, metres, author }) => {
    const dispatch = useDispatch();
    const [isFront, setIsFront] = useState(false); // Состояние для отслеживания порядка слоев

    const handleRemoveMarker = async () => {
        // Удаление маркера
    }

    const handleMarkerClick = () => {
        // Перемещение маркера на передний план
        setIsFront(true);
    }

    return (
        <LayersControl.Overlay
            checked={true}
            name={category}
        >
            <FeatureGroup pathOptions={{
                color: category === 'Убийство' ? 'red' :
                    category === 'Авария' ? 'blue' :
                        category === 'Кража' ? 'green' : 'red',
            }}>
                <Popup>
                    Название: {markerName} <br />
                    Категория: {category} <br />
                    Описание: {description} <br />
                    Author: {author} <br />
                    id: {_id} <br />
                    <button onClick={handleRemoveMarker}>Удалить</button>
                </Popup>
                <Circle center={position} radius={metres} />

                {/* Обработчик клика на маркере */}
                <Marker
                    position={position}
                    eventHandlers={{
                        click: () => {
                            handleMarkerClick();
                        }
                    }}
                    icon={new L.icon({
                        iconUrl: greenMarker,
                        iconSize: [32, 32],
                        iconAnchor: [16, 32],
                    })}
                    zIndexOffset={isFront ? 1000 : 0} // Устанавливаем zIndex для порядка слоев
                />
            </FeatureGroup>
        </LayersControl.Overlay>
    )
}

export default NewMarker;
