import React, { useState } from 'react';
import axios from '../utils/axios';
import { Popup, Circle, LayersControl, FeatureGroup } from 'react-leaflet';
import { useDispatch } from 'react-redux';
import { removeMarker } from '../redux/features/markerSlice';

const NewMarker = ({ _id, category, markerName, description, position, metres, author }) => {
    const dispatch = useDispatch();
    const [bounds, setBounds] = useState([position]);
    console.log('bounds', bounds)
    const map = new Map();

    const innerHandlers = useMemo(
        () => ({
            click() {
                setBounds(innerBounds)
                map.fitBounds(innerBounds)
            },
        }),
        [map],
    )
    const outerHandlers = useMemo(
        () => ({
            click() {
                setBounds(outerBounds)
                map.fitBounds(outerBounds)
            },
        }),
        [map],
    )

    const handleRemoveMarker = async () => {
        try {
            const response = await axios.delete(`/markers/${_id}`);

            if (response.status === 200) {
                dispatch(removeMarker(_id))
            }

            window.location.reload();
        } catch (error) {
            if (error.response && error.response.status === 403) {
                console.error("You are not authorized to delete this marker");
            } else {
                console.error("An error occurred:", error);
            }
        }
    }

    return (
        <LayersControl.Overlay
            checked={true}
            name={category}
        >
            <FeatureGroup pathOptions={{
                color: category === 'Убийство' ? 'red' :
                    category === 'Авария' ? 'blue' :
                        category === 'Кража' ? 'green' : 'red',
            }}>
                <Popup>
                    Название: {markerName} <br />
                    Категория: {category} <br />
                    Описание: {description} <br />
                    Author: {author} <br />
                    id: {_id} <br />
                    <button onClick={handleRemoveMarker}>Удалить</button>
                </Popup>
                <Circle center={position} radius={metres} />
            </FeatureGroup>
        </LayersControl.Overlay>
    )
}

export default NewMarker
