import React, { useState } from 'react'
import { createMarker } from '../redux/features/markerSlice';
import { useDispatch } from 'react-redux';

const ModalWindow = ({ handleChangeModal }) => {
    const [markerName, setMarkerName] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');

    const testPosition = [44.3518889, 43.3644363]

    const dispatch = useDispatch();

    const submitHandler = () => {
        try {
            const data = new FormData();
            data.append('markerName', markerName);
            data.append('category', category);
            data.append('description', description);
            data.append('position', testPosition);

            dispatch(createMarker(data));
        } catch (error) {
            console.log(`send error - ${error}`);
        }
    }

    const clearFormHandler = () => {
        setMarkerName('');
        setCategory('');
        setDescription('');
    };

    return (
        <form className='w-screen h-screen absolute bg-black text-white' onSubmit={e => e.preventDefault()}>
            <label htmlFor='name'>
                <span>Название:</span>
                <input type="text" name="name" id="name" />
            </label>
            <label htmlFor='category'>
                <span>Категория:</span>
                <input type="text" name="category" id="category" />
            </label>
            <label htmlFor='description'>
                <span>Описание:</span>
                <input type="text" name="description" id="description" />
            </label>

            <button onClick={handleChangeModal}>Exit</button>
            <button onClick={submitHandler}>Add</button>
            <button onClick={clearFormHandler}>Clear</button>
        </form>
    )
}

export default ModalWindow
