import { MapContainer, Popup, TileLayer, useMap, Marker } from 'react-leaflet';
import { ArchiveBoxArrowDownIcon } from '@heroicons/react/24/solid';
import L from 'leaflet';

const Map = () => {
    const position = [51.505, -0.09]; // your location - change

    const customMarker = new L.icon({
        iconUrl: require('./icons/custom-marker.png'),
        iconSize: [32, 32], // Размер иконки в пикселях
        iconAnchor: [16, 32], // Точка, вокруг которой будет вращаться иконка
    })

    const markerPositions = [
        [51.505, -0.09],
        [51.515, -0.1],
        [51.495, -0.08],
    ];

    return (
        <MapContainer center={position} zoom={13} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            <Marker position={position} icon={customMarker}>
                <Popup>A sample marker on the map.</Popup>
            </Marker>

            {markerPositions.map((mapEl, index) => (
                <Marker key={index} position={mapEl}>
                    <Popup>Marker {index + 1}</Popup>
                </Marker>
            ))}
        </MapContainer>

    )
}

export default Map
