import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { checkIsAuth, loginUser } from '../redux/features/authSlice';
import { toast } from 'react-toastify';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const isAuth = useSelector(checkIsAuth);

    const { status } = useSelector(state => state.auth);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        if (status) toast(status);
        if (isAuth) navigate('/');
    }, [status, isAuth, navigate]);

    const handleSubmit = () => {
        try {
            dispatch(loginUser({ email, password }))
                .catch((error) => {
                    if (error.response) {
                        // Ошибка с ответом от сервера
                        console.log("Ошибка сервера:", error.response.data);
                    } else if (error.request) {
                        // Ошибка без ответа от сервера
                        console.log("Сервер не ответил");
                    } else {
                        // Ошибка при настройке запроса
                        console.log("Ошибка настройки запроса:", error.message);
                    }
                });
        } catch (error) {
            console.log(error);
        }
    };


    return (
        <form>
            <div>
                <label>
                    Email:
                    <input
                        type="email"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        placeholder='email'
                    />
                </label>
                <label>
                    Password:
                    <input
                        type="password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        placeholder='password'
                    />
                </label>
            </div>

            <div>
                <button
                    type="submit"
                    onClick={handleSubmit}
                >
                    Войти
                </button>
                <Link
                    to="/register"
                >
                    Нет аккаунта?
                </Link>
            </div>
        </form>
    );
};

export default Login;
