import React, { useState } from 'react';
import { createMarker } from '../redux/features/markerSlice';
import { useDispatch } from 'react-redux';

const ModalWindow = ({ handleChangeModal, userLocation }) => {
    const [markerName, setMarkerName] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [metresWindow, setMetresWindow] = useState();

    const dispatch = useDispatch();

    const submitHandler = () => {
        try {
            const data = new FormData();
            data.append('markerName', markerName);
            data.append('category', category);
            data.append('description', description);
            data.append('position', JSON.stringify(userLocation));
            data.append('metres', metresWindow);

            console.log('Data to be sent:', Object.fromEntries(data.entries()));
            dispatch(createMarker(data));

            setMarkerName('');
            setCategory('');
            setDescription('');

            window.location.reload();
        } catch (error) {
            console.log(`send error - ${error}`);
        }
    };

    const clearFormHandler = () => {
        setMarkerName('');
        setCategory('');
        setDescription('');
    };

    return (
        <div className='absolute bottom-0 left-0'>
            <form
                onSubmit={e => {
                    e.preventDefault();
                }}
            >
                <label htmlFor="name">
                    <span>Название:</span>
                    <input
                        type="text"
                        name="name"
                        id="name"
                        placeholder='Название'
                        value={markerName}
                        onChange={e => setMarkerName(e.target.value)}
                    />
                </label>
                <label htmlFor="category">
                    <span>Категория:</span>
                    <input
                        type="text"
                        name="category"
                        id="category"
                        placeholder='Название'
                        value={category}
                        onChange={e => setCategory(e.target.value)}
                    />
                </label>
                <label htmlFor="description">
                    <span>Описание:</span>
                    <input
                        type="text"
                        name="description"
                        id="description"
                        placeholder='Название'
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                    />
                </label>

                <label htmlFor="metres">
                    <span>Радиус действия (в метрах):</span>
                    <input
                        type="text"
                        name="metres"
                        id="metres"
                        placeholder='Радиус действия'
                        value={metresWindow}
                        onChange={e => setMetresWindow(e.target.value)}
                    />
                </label>

                <div>
                    <button className='ml-8' onClick={handleChangeModal}>Exit</button>
                    <button type='submit' className='ml-8' onClick={submitHandler}>Add</button>
                    <button className='ml-8' onClick={clearFormHandler}>Clear</button>
                </div>


            </form>
        </div>

    );
};

export default ModalWindow;
