import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import axios from '../../utils/axios';

const initialState = {
    markers: [],
    currentMarkers: [],
    loading: false,
};

export const createMarker = createAsyncThunk(
    'marker/createMarker',
    async params => {
        try {
            const { data } = await axios.put('/markers', params);
            return data;
        } catch (error) {
            console.log(error);
        }
    }
);

export const getAllMarkers = createAsyncThunk(
    'marker/getAllMarkers',
    async () => {
        try {
            const { data } = await axios.get('/markers');
            return data;
        } catch (error) {
            console.log(error);
        }
    }
);

export const removeMarker = createAsyncThunk(
    'marker/removeMarker',
    async id => {
        try {
            const { data } = await axios.delete(`/markers/${id}`, id);
            return data;
        } catch (error) {
            console.log(error);
        }
    }
);

