import { MapContainer, Popup, TileLayer, useMap, Marker } from 'react-leaflet';
import { ArchiveBoxArrowDownIcon } from '@heroicons/react/24/solid';
import L from 'leaflet';
import redMarker from '../assets/markers/red_marker.png';
import blueMarker from '../assets/markers/blue_marker.png';
import greenMarker from '../assets/markers/green_marker.png';
import { useState, useEffect } from 'react';

const Map = () => {
    const [userLocation, setUserLocation] = useState(null);

    const userPositionCoords = [51.505, -0.09]; // your location - change

    // const customIcon = new L.icon({
    //     iconUrl: redMarker,
    //     iconSize: [32, 32], // Размер иконки в пикселях
    //     iconAnchor: [16, 32], // Точка, вокруг которой будет вращаться иконка
    // })

    // const markerPositions = [
    //     [44.1518889, 43.1644363],
    //     [45.2518889, 43.2644363],
    //     [44.3518889, 43.3644363],
    // ];

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;


                    console.log('position current lat', latitude)
                    console.log('position current long', longitude)
                    setUserLocation([latitude, longitude])
                },
                (error) => {
                    console.error('Error getting user location:', error);
                }
            )
        } else {
            console.error('Geolocation is not supported by this browser.');
        }
    }, []);


    console.log('user location is', userLocation)


    const markerPositions = [
        {
            name: 'Труп человека',
            category: 'Убийство',
            description: 'Труп человека найден у кафе',
            position: [44.0518889, 43.1644363],
            id: 1,
        },
        {
            name: '2 машины в хлам',
            category: 'Авария',
            description: 'да я хз, проезжал мимо, увидел 2 разбитые машины',
            position: [44.2518889, 43.2644363],
            id: 2,
        },
        {
            name: 'Пропала сумка',
            category: 'Кража',
            description: 'Наверняка какой-то цыган спёр!!!',
            position: [44.3518889, 43.3644363],
            id: 3,
        },
    ];

    if (userLocation === null) {
        return <div>Loading...</div>;
    }

    return (
        <MapContainer center={userLocation} zoom={13} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />

            <Marker position={userLocation} icon={new L.icon({
                iconUrl: redMarker,
                iconSize: [32, 32],
                iconAnchor: [16, 32],
            })}>
                <Popup>A sample marker on the map.</Popup>
            </Marker>

            {markerPositions.map((mapEl) => (
                <Marker key={mapEl.id} position={mapEl.position} icon={new L.icon({
                    iconUrl:
                        mapEl.category === 'Убийство' ? redMarker :
                            mapEl.category === 'Авария' ? blueMarker :
                                mapEl.category === 'Кража' ? greenMarker : redMarker,
                    iconSize: [32, 32],
                    iconAnchor: [16, 32],
                })}>
                    <Popup>
                        Название: {mapEl.name} <br />
                        Категория: {mapEl.category} <br />
                        Описание: {mapEl.description}
                    </Popup>
                </Marker>
            ))}
        </MapContainer>

    )
}

export default Map
