import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { checkIsAuth, registerUser } from '../redux/features/authSlice';
import { toast } from 'react-toastify';
import axios from '../utils/axios'

const Registration = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');
    const [city, setCity] = useState('');
    const [picturePath, setPicturePath] = useState();

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const isAuth = useSelector(checkIsAuth);
    const { status } = useSelector(state => state.auth);

    useEffect(() => {
        if (status) toast(status);
        if (isAuth) navigate('/');
    }, [status, isAuth, navigate]);

    const handleSubmit = () => {
        try {
            const formData = new FormData();
            formData.append('email', email);
            formData.append('password', password);
            formData.append('fullName', fullName);
            formData.append('city', city);
            formData.append('picture', picturePath);

            axios.post('/auth/register', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            })
                .then(response => {
                    if (response.token) {
                        window.localStorage.setItem('token', response.token);
                    }

                    return response;
                })
                .catch(error => {
                    console.log(error)
                });


            // dispatch(registerUser({ email, password, fullName, city, picturePath }));
            setEmail('');
            setPassword('');
            setFullName('');
            setCity('');
        } catch (e) {
            console.log(e);
        }
    }

    return (
        <form
            onSubmit={e => e.preventDefault()}
            encType='multipart/form-data'
        >
            <h1>Registration</h1>

            <div>
                <label>
                    Full name:
                    <input
                        type="text"
                        placeholder='Name'
                        value={fullName}
                        onChange={e => setFullName(e.target.value)}
                    />
                </label>

                <label>
                    Emai:
                    <input
                        type="email"
                        placeholder='Email'
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                </label>

                <label>
                    Password:
                    <input
                        type="password"
                        placeholder='Password'
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                </label>

                <label>
                    City:
                    <input
                        type="text"
                        placeholder='City'
                        value={city}
                        onChange={e => setCity(e.target.value)}
                    />
                </label>


                <label>
                    Avatar:
                    <input
                        type="file"
                        accept="image/*"
                        name="picture" // Важно, должно совпадать с upload.single('picture')
                        onChange={e => setPicturePath(e.target.files[0])}
                    />

                </label>
            </div>

            <div>
                <button
                    onClick={handleSubmit}
                    type='submit'
                >
                    Submit
                </button>

                <Link
                    to="/login"
                >
                    Уже зарегистрирован?
                </Link>
            </div>
        </form>
    )
}

export default Registration
