import { MapContainer, Popup, TileLayer, Marker, LayerGroup, Circle, LayersControl, FeatureGroup, Rectangle } from 'react-leaflet';
import { ArchiveBoxArrowDownIcon } from '@heroicons/react/24/solid';
import L, { map } from 'leaflet';
import redMarker from '../assets/markers/red_marker.png';
import blueMarker from '../assets/markers/blue_marker.png';
import greenMarker from '../assets/markers/green_marker.png';
import myGeo from '../assets/main/myGeo.svg';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllMarkers } from '../redux/features/markerSlice';
import { useCallback, useRef } from 'react';
import NewMarker from './NewMarker';

const Map = ({ userLocation, selectedMarkerPosition }) => {
    const dispatch = useDispatch();
    const { currentMarkers } = useSelector(state => state.markers);
    const mapRef = useRef();
    const [backToMyGeo, setBackToMyGeo] = useState();

    const fetchMarkers = useCallback(async () => {
        try {
            dispatch(getAllMarkers());
        } catch (error) {
            console.log(error);
        }
    });

    useEffect(() => {
        fetchMarkers()
    }, [dispatch]);

    useEffect(() => {
        if (mapRef.current && selectedMarkerPosition) {
            mapRef.current.setView(selectedMarkerPosition, mapRef.current.getZoom());
        }
    }, [selectedMarkerPosition]);

    useEffect(() => {
        if (backToMyGeo) {
            setBackToMyGeo(userLocation)
        }
    }, []);

    console.log(backToMyGeo)

    if (userLocation === null) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <div className='absolute z-20 top-[40%] right-2'>
                <img
                    className='w-[50px] h-[50px] drop-shadow-xl'
                    src={myGeo}
                    alt="myGeo"
                />
            </div>

            <MapContainer ref={mapRef} center={userLocation || selectedMarkerPosition} zoom={13} className='z-10 min-h-[700px] w-full relative'>
                <TileLayer
                    url="https://{s}.basemaps.cartocdn.com/rastertiles/voyager/{z}/{x}/{y}.png"
                    attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
                />

                <Marker position={userLocation} icon={new L.icon({
                    iconUrl: redMarker,
                    iconSize: [32, 32],
                    iconAnchor: [16, 32],
                })}>
                    <Popup>Ваше местоположение:</Popup>
                </Marker>

                <LayersControl position='bottomleft'>
                    {currentMarkers.map((mapEl) => (
                        <NewMarker
                            key={mapEl._id}
                            _id={mapEl._id}
                            category={mapEl.category}
                            description={mapEl.description}
                            markerName={mapEl.markerName}
                            position={mapEl.position}
                            metres={mapEl.metres || 20}
                            author={mapEl.author}
                            createdAt={mapEl.createdAt}
                        />
                    ))}

                </LayersControl>



                {/* {currentMarkers.map((mapEl) => (
                <Marker key={mapEl._id} position={mapEl.position} icon={new L.icon({
                    iconUrl:
                        mapEl.category === 'Убийство' ? redMarker :
                            mapEl.category === 'Авария' ? blueMarker :
                                mapEl.category === 'Кража' ? greenMarker : blueMarker,
                    iconSize: [32, 32],
                    iconAnchor: [16, 32],
                })}>
                    <Popup>
                        Название: {mapEl.markerName} <br />
                        Категория: {mapEl.category} <br />
                        Описание: {mapEl.description}
                    </Popup>
                </Marker>
            ))} */}
            </MapContainer>

        </div>

    )
}

export default Map
