import React, { useState } from 'react';
import { createMarker } from '../redux/features/markerSlice';
import { useDispatch } from 'react-redux';

import redEllipse from '../assets/ellipses/red_ellipse.svg';
import blueEllipse from '../assets/ellipses/blue_ellipse.svg';
import greenEllipse from '../assets/ellipses/green_ellipse.svg';
import purpleEllipse from '../assets/ellipses/purple_ellipse.svg';
import yellowEllipse from '../assets/ellipses/yellow_ellipse.svg';

import { CheckIcon } from '@heroicons/react/24/solid';

const ModalWindow = ({ userLocation }) => {
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [metresWindow, setMetresWindow] = useState();

    const dispatch = useDispatch();

    const submitHandler = () => {
        try {
            const data = new FormData();
            data.append('category', category);
            data.append('description', description);
            data.append('position', JSON.stringify(userLocation));
            data.append('metres', metresWindow);

            console.log('Data to be sent:', Object.fromEntries(data.entries()));
            dispatch(createMarker(data));

            setCategory('');
            setDescription('');

            window.location.reload();
        } catch (error) {
            console.log(`send error - ${error}`);
        }
    };

    // const clearFormHandler = () => {
    //     setCategory('');
    //     setDescription('');
    // };


    return (
        <div className="bg-white pt-2 flex flex-col absolute bottom-0 left-0 z-50 w-full text-center">
            <div className='mx-auto w-[80%]'>
                <h1 className='font-medium text-xl'>Сообщить об опасности</h1>

                <div className='flex flex-col items-start'>
                    {/* <label htmlFor="category1">
                        <input type="radio" name="category" id="category1" />
                        <span>asd1</span>
                    </label> */}


                </div>

                <form
                    onSubmit={e => {
                        e.preventDefault();
                    }}
                    className='flex flex-col gap-3 text-left items-start mt-5'
                >
                    <div className='flex flex-col gap-2 text-sm'>
                        <div
                            className='flex gap-1 items-center cursor-pointer'
                            onClick={() => setCategory('Угроза жизни')}
                        >
                            <img src={redEllipse} alt="redEllipse" />
                            <span>Угроза жизни</span>
                            {category === 'Угроза жизни' && <CheckIcon className='w-3 h-3' />}
                        </div>

                        <div
                            className='flex gap-1 items-center cursor-pointer'
                            onClick={() => setCategory('Неадекватное поведение')}
                        >
                            <img src={yellowEllipse} alt="yellowEllipse" />
                            <span>Неадекватное поведение</span>
                            {category === 'Неадекватное поведение' && <CheckIcon className='w-3 h-3' />}
                        </div>

                        <div
                            className='flex gap-1 items-center cursor-pointer'
                            onClick={() => setCategory('Домагательства')}
                        >
                            <img src={purpleEllipse} alt="purpleEllipse" />
                            <span>Домагательства</span>
                            {category === 'Домагательства' && <CheckIcon className='w-3 h-3' />}
                        </div>

                        <div
                            className='flex gap-1 items-center cursor-pointer'
                            onClick={() => setCategory('Проблемы с инфраструктурой')}
                        >
                            <img src={blueEllipse} alt="blueEllipse" />
                            <span>Проблемы с инфраструктурой</span>
                            {category === 'Проблемы с инфраструктурой' && <CheckIcon className='w-3 h-3' />}
                        </div>

                        <div
                            className='flex gap-1 items-center cursor-pointer'
                            onClick={() => setCategory('Авария')}
                        >
                            <img src={greenEllipse} alt="greenEllipse" />
                            <span>Авария</span>
                            {category === 'Авария' && <CheckIcon className='w-3 h-3' />}
                        </div>
                    </div>


                    <div >
                        <p>В скольких метрах от вас находится опасность?</p>
                        <div className='mt-2 flex gap-5'>
                            <label htmlFor="one_hundred">
                                <input onClick={() => setMetresWindow(50)} type="radio" name="distance" id="one_hundred" />
                                50
                            </label>
                            <label htmlFor="two_hundreds">
                                <input onClick={() => setMetresWindow(100)} type="radio" name="distance" id="two_hundreds" />
                                100
                            </label>
                            <label htmlFor="three_hundreds">
                                <input onClick={() => setMetresWindow(200)} type="radio" name="distance" id="three_hundreds" />
                                200
                            </label>
                        </div>
                    </div>


                    <label htmlFor="description">
                        <span>Описание:</span>
                        <input
                            type="text"
                            name="description"
                            id="description"
                            placeholder="Название"
                            value={description}
                            onChange={e => setDescription(e.target.value)}
                        />
                    </label>

                    {/* <label htmlFor="metres">
                        <span>Радиус действия (в метрах):</span>
                        <input
                            type="text"
                            name="metres"
                            id="metres"
                            placeholder="Радиус действия"
                            value={metresWindow}
                            onChange={e => setMetresWindow(e.target.value)}
                        />
                    </label> */}

                    <div>
                        <button type="submit" className="ml-8" onClick={submitHandler}>
                            Add
                        </button>
                        {/* <button className='ml-8' onClick={clearFormHandler}>Clear</button> */}
                    </div>
                </form>
            </div>

        </div>
    );
};

export default ModalWindow;
