import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { checkIsAuth, loginUser } from '../redux/features/authSlice';
import { toast } from 'react-toastify';

import logo from '../assets/main/Logo.svg';
import avatar_registration from '../assets/main/avatar_registration.png';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const isAuth = useSelector(checkIsAuth);

    const { status } = useSelector(state => state.auth);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        try {
            if (status) toast(status);
            if (isAuth) navigate('/');
        } catch (error) {
            console.log(`error in useEffect - ${error}`)
        }
    }, [status, isAuth, navigate]);

    const handleSubmit = () => {
        try {
            dispatch(loginUser({ email, password }));
        } catch (error) {
            console.log(`error in handleSubmit - ${error}`)
        }
    };


    return (
        <section className='flex min-h-screen bg-white py-3 px-5'>
            <div className="logo">
                <img className='w-16 h-16' src={logo} alt="logo" />
            </div>

            <div className="flex-1 flex justify-center items-center">
                <form onSubmit={e => e.preventDefault()} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
                    <h1 className="text-xl mb-4">Регистрация</h1>
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">
                            Email:
                            <input
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                type="email"
                                value={email}
                                onChange={e => setEmail(e.target.value)}
                                placeholder='email'
                            />
                        </label>
                        <label className="block text-gray-700 text-sm font-bold mb-2">
                            Password:
                            <input
                                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                                type="password"
                                value={password}
                                onChange={e => setPassword(e.target.value)}
                                placeholder='password'
                            />
                        </label>
                    </div>
                    <div className="flex items-center justify-between">
                        <button
                            type="submit"
                            onClick={handleSubmit}
                            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        >
                            Войти
                        </button>
                        <Link
                            to="/register"
                            className="inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800"
                        >
                            Нет аккаунта?
                        </Link>
                    </div>
                </form>
            </div>
        </section>

    );
};

export default Login;
