import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { checkIsAuth, registerUser } from '../redux/features/authSlice';
import { toast } from 'react-toastify';

import logo from '../assets/main/Logo.svg';
import avatar_registration from '../assets/main/avatar_registration.svg';


const Registration = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');
    const [city, setCity] = useState('');
    const [picturePath, setPicturePath] = useState();

    const dispatch = useDispatch();
    const navigate = useNavigate();

    const isAuth = useSelector(checkIsAuth);
    const { status } = useSelector(state => state.auth);

    useEffect(() => {
        if (status) toast(status);
        if (isAuth) navigate('/');
    }, [status, isAuth, navigate]);

    const handleSubmit = () => {
        try {
            dispatch(registerUser({ email, password, fullName, city, picturePath }));
            setEmail('');
            setPassword('');
            setFullName('');
            setCity('');
        } catch (e) {
            console.log(e);
        }
    }

    return (
        <section className='flex flex-col min-h-screen bg-white py-3 px-5 text-2xl font-roboto font-extralight'>
            <form
                onSubmit={e => e.preventDefault()}
                encType='multipart/form-data'
            >
                <h1>Registration</h1>

                <div>
                    <label>
                        Full name:
                        <input
                            type="text"
                            placeholder='Name'
                            value={fullName}
                            onChange={e => setFullName(e.target.value)}
                        />
                    </label>

                    <label>
                        Emai:
                        <input
                            type="email"
                            placeholder='Email'
                            value={email}
                            onChange={e => setEmail(e.target.value)}
                        />
                    </label>

                    <label>
                        Password:
                        <input
                            type="password"
                            placeholder='Password'
                            name="password"
                            value={password}
                            onChange={e => setPassword(e.target.value)}
                        />
                    </label>

                    <label>
                        City:
                        <input
                            type="text"
                            placeholder='City'
                            value={city}
                            onChange={e => setCity(e.target.value)}
                        />
                    </label>
                </div>

                <div>
                    <button
                        onClick={handleSubmit}
                        type='submit'
                    >
                        Submit
                    </button>

                    <Link
                        to="/login"
                    >
                        Уже зарегистрирован?
                    </Link>
                </div>
            </form>
        </section>

    )
}

export default Registration
