import React, { useState } from 'react';
import { editUserProfile, logOut } from '../redux/features/authSlice';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { XMarkIcon } from '@heroicons/react/24/solid';

import logo from '../assets/main/Logo.svg';
import avatar_registration from '../assets/main/avatar_registration.svg';

import { toast } from 'react-toastify';

const UserProfile = ({ user, showMenu, setShowMenu }) => {
    const [fullName, setFullName] = useState(user.fullName);
    const [email, setEmail] = useState(user.email);
    const [city, setCity] = useState(user.city);

    const dispatch = useDispatch();

    const handleSubmit = () => {
        const updatedProfile = {
            fullName,
            email,
            city,
        };

        dispatch(editUserProfile(updatedProfile));
    };

    const handleLogOut = () => {
        dispatch(logOut());
        window.localStorage.removeItem('token');
        toast('Вы вышли из системы');
    }


    return (

        <div className={`bg-white text-2xl font-roboto font-extralight w-full flex flex-col justify-center items-center min-h-full ${showMenu ? 'translate-x-[0%]' : 'translate-x-[100%]'} translate-x-[100%] ease-in duration-300 z-50 absolute top-0 right-0`}>
            <XMarkIcon className='absolute z-40 w-8 h-w-8 right-0 top-2' onClick={() => setShowMenu(prev => !prev)} />
            <form onSubmit={handleSubmit}>
                <h1>Профиль</h1>
                <img src={avatar_registration} alt="avatar_registration" />
                <ul>
                    <li>
                        Name: {user.fullName} -
                        <input type="text" value={fullName} onChange={e => setFullName(e.target.value)} name="" id="" />
                    </li>
                    <li>
                        Email: {user.email} -
                        <input type="email" value={email} onChange={e => setEmail(e.target.value)} name="" id="" />
                    </li>
                    <li>
                        City: {user.city} -
                        <input type="text" value={city} onChange={e => setCity(e.target.value)} name="" id="" />
                    </li>
                    {/* <li>
                    ID: {user._id} -
                </li> */}
                </ul>
                <div>
                    <button type="submit">Save Changes</button>
                    <Link to={`/login`} onClick={handleLogOut}>Log out</Link>

                </div>
            </form>
        </div>



    )
}

export default UserProfile
