import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { logOut, getMe } from '../redux/features/authSlice';
import { toast } from 'react-toastify';
import Map from '../components/Map';
import { getAllMarkers } from '../redux/features/markerSlice';
import ModalWindow from '../components/ModalWindow';
import { Link } from 'react-router-dom';
import NearMarkers from '../components/NearMarkers';
import { useMapEvent } from 'react-leaflet';
import UserProfile from '../components/UserProfile';
import { Bars3Icon } from '@heroicons/react/24/solid';


const Home = () => {
    const dispatch = useDispatch();
    const [openModal, setOpenModal] = useState(false);
    const [userLocation, setUserLocation] = useState(null);
    const [loadingUser, setLoadingUser] = useState(true);
    const [selectedMarkerPosition, setSelectedMarkerPosition] = useState(null);
    const [showNearMarkers, setShowNearMarkers] = useState(false);

    const [showMenu, setShowMenu] = useState(false);


    const handleMarkerClick = (position) => {
        setSelectedMarkerPosition(position);
    };

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const { latitude, longitude } = position.coords;
                    setUserLocation([latitude, longitude])
                },
                (error) => {
                    console.error('Error getting user location:', error);
                }
            )
        } else {
            console.error('Geolocation is not supported by this browser.');
        }

        dispatch(getMe())
        setLoadingUser(false)
    }, []);

    const handleChangeModal = () => {
        setOpenModal(prev => !prev)
    }

    const { user } = useSelector(state => state.auth);

    const handleLogOut = () => {
        dispatch(logOut());
        window.localStorage.removeItem('token');
        toast('Вы вышли из системы');
    }

    console.log('showMenu', showMenu)

    return (
        <div>
            <h1>Home</h1>
            <div>
                <button onClick={handleChangeModal}>Open Modal</button>
                {
                    openModal && (
                        <ModalWindow userLocation={userLocation} handleChangeModal={handleChangeModal} />
                    )
                }
            </div>

            {/* <div>
                Моё местоположение: {userLocation[0]} {userLocation[1]}
            </div> */}

            <div className='w-full h-[700px] relative'>
                {/* <div className='absolute z-40 w-full h-[20px] -bottom-0.5 right-0 bg-white'></div> */}
                <Bars3Icon onClick={() => setShowMenu(prev => !prev)} className='absolute z-40 w-8 h-w-8 right-0 top-2' />
                <Map userLocation={userLocation} selectedMarkerPosition={selectedMarkerPosition} setShowNearMarkers={setShowNearMarkers} />
            </div>

            {loadingUser ? (
                <p>Loading user data...</p>
            ) : (
                user && (
                    <UserProfile user={user} />
                )
            )}

            {showNearMarkers && (
                <NearMarkers userLocation={userLocation} onMarkerClick={handleMarkerClick} />
            )}


            <Link to={`/login`} onClick={handleLogOut}>Log out</Link>
        </div>
    )
}

export default Home
