import React, { useState } from 'react';
import { createMarker } from '../redux/features/markerSlice';
import { useDispatch } from 'react-redux';

const ModalWindow = ({ handleChangeModal, userLocation, onData }) => {
    const [markerName, setMarkerName] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [metres, setMetres] = useState();

    const [dataToSend, setDataToSend] = useState('Данные из дочернего компонента');

    // Функция для отправки данных в родительский компонент
    const sendDataToParent = () => {
        onData(dataToSend);
    };

    const dispatch = useDispatch();

    console.log("userLocation", userLocation)

    const submitHandler = () => {
        try {
            const data = new FormData();
            data.append('markerName', markerName);
            data.append('category', category);
            data.append('description', description);
            data.append('position', JSON.stringify(userLocation));

            // console.log('submit handler data', Object.fromEntries(data.entries()));
            dispatch(createMarker(data));
            sendDataToParent();

            setMarkerName('');
            setCategory('');
            setDescription('');

            window.location.reload();
        } catch (error) {
            console.log(`send error - ${error}`);
        }
    };

    const clearFormHandler = () => {
        setMarkerName('');
        setCategory('');
        setDescription('');
    };

    return (
        <form
            className=""
            onSubmit={e => e.preventDefault()}
        >
            <label htmlFor="name">
                <span>Название:</span>
                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder='Название'
                    value={markerName}
                    onChange={e => setMarkerName(e.target.value)}
                />
            </label>
            <label htmlFor="category">
                <span>Категория:</span>
                <input
                    type="text"
                    name="category"
                    id="category"
                    placeholder='Название'
                    value={category}
                    onChange={e => setCategory(e.target.value)}
                />
            </label>
            <label htmlFor="description">
                <span>Описание:</span>
                <input
                    type="text"
                    name="description"
                    id="description"
                    placeholder='Название'
                    value={description}
                    onChange={e => setDescription(e.target.value)}
                />
            </label>

            <label htmlFor="metres">
                <span>Радиус действия (в метрах):</span>
                <input
                    type="text"
                    name="metres"
                    id="metres"
                    placeholder='Радиус действия'
                    value={metres}
                    onChange={e => setMetres(e.target.value)}
                />
            </label>

            <div>
                <button className='ml-8' onClick={handleChangeModal}>Exit</button>
                <button className='ml-8' onClick={submitHandler}>Add</button>
                <button className='ml-8' onClick={clearFormHandler}>Clear</button>
            </div>


        </form>
    );
};

export default ModalWindow;
