import React, { useState } from 'react'
import { useSelector } from 'react-redux';

const NearMarkers = ({ userLocation }) => {
    const { currentMarkers } = useSelector(state => state.markers);
    const [distance, setDistance] = useState([]);

    console.log('near markers', currentMarkers);
    console.log('user loc', userLocation);

    currentMarkers.map((currentMark) => {
        const calculateDistance = (latUser, longUser, latMark, longMark) => {
            const R = 6371; // Радиус Земли в километрах
            const dLat = (lat2 - lat1) * (Math.PI / 180); // Разница широт в радианах
            const dLon = (lon2 - lon1) * (Math.PI / 180); // Разница долгот в радианах
        }
    })

    return (
        <div>
            <h1>Маркеры поблизости:</h1>
            <ul>
                {
                    currentMarkers.map((marker) => (
                        <li key={marker._id}>
                            {marker.markerName}
                            {marker.description} -
                            {distance}
                        </li>
                    ))
                }
            </ul>
        </div>
    )
}

export default NearMarkers
