import React, { useState } from 'react'
import axios from '../utils/axios';
import { Popup, Circle, LayersControl, FeatureGroup, Marker } from 'react-leaflet';
import L, { map } from 'leaflet';
import { useDispatch, useSelector } from 'react-redux';
import { removeMarker } from '../redux/features/markerSlice';
import blueMarker from '../assets/markers/blue_marker.png';

const NewMarker = ({ _id, category, markerName, description, position, metres, author, createdAt }) => {
    const dispatch = useDispatch();
    const { user } = useSelector(state => state.auth);
    console.log(user, id)
    const [isFront, setIsFront] = useState(false);

    const handleRemoveMarker = async () => {
        try {
            const response = await axios.delete(`/markers/${_id}`);

            if (response.status === 200) {
                dispatch(removeMarker(_id))
            }

            window.location.reload();
        } catch (error) {
            if (error.response && error.response.status === 403) {
                console.error("You are not authorized to delete this marker");
            } else {
                console.error("An error occurred:", error);
            }
        }
    }

    const handleMarkerClick = () => {
        setIsFront(true);
    }

    return (
        <LayersControl.Overlay
            checked={true}
            name={category}
        >
            <FeatureGroup pathOptions={{
                color: category === 'Убийство' ? 'red' :
                    category === 'Авария' ? 'blue' :
                        category === 'Кража' ? 'green' : 'red',
            }}>
                <Popup>
                    Название: {markerName} <br />
                    Категория: {category} <br />
                    Описание: {description} <br />
                    Author: {author} <br />
                    id: {_id} <br />
                    {user._id === _id && (
                        <button onClick={handleRemoveMarker}>Удалить</button>
                    )}
                </Popup>
                <Circle center={position} radius={metres} />

                {/* Обработчик клика на маркере
                <Marker
                    position={position}
                    eventHandlers={{
                        click: () => {
                            handleMarkerClick();
                        }
                    }}
                    icon={new L.icon({
                        iconUrl: blueMarker,
                        iconSize: [32, 32],
                        iconAnchor: [16, 32],
                    })}
                    zIndexOffset={isFront ? 1000 : 0} // Устанавливаем zIndex для порядка слоев
                /> */}
            </FeatureGroup>
        </LayersControl.Overlay>
    )
}

export default NewMarker
