        // Отправьте обновленные данные на сервер и обработайте их здесь

        // После успешного сохранения, вы можете установить isEditing в false
        setIsEditing(false);
    };


    return (
        <ul>
            <li>Name: {user.fullName}</li>
            <li>Email: {user.email}</li>
            <li>City: {user.city}</li>
            <li>ID: {user._id}</li>
        </ul>
    )
}

